import { Form, Input } from "antd";


function InputTask({ onAdd }) {

    function submitTask(event) {
        console.log( event )
        if (event) {
            onAdd(event.value);
        }   
        event.value = ""
    }

    return (
        <Form onFinish={submitTask} >
            <Input
                placeholder="Your task..."
            />
        </Form>
    );
}

export default InputTask;
