import { Input } from "antd";


function InputTask({ onAdd }) {

    function submitTask(event) {
        console.log(event)
        const name = event.target.value.trim();
        if (name) {
            onAdd(name);
            event.target.value = "";
        }   
    }

    return (
        <Input.Search
            placeholder="Your task..."
            allowClear
            enterButton="Add"
            onSearch={submitTask}
        />
    );
}

export default InputTask;
