import './App.css';
import { Button, List, Input, Checkbox } from 'antd';
import axios from "axios";
import React, { useEffect, useState } from 'react';
import InputTask from './components/InputTask';
import { ListTask } from './components/ListTask';

function App() {
  // var tasks = []
  // const getTask = () => {
  //   axios
  //     .get("http://localhost:8000/api/tasks/")
  //     .then( (res) => console.log(res) )
  // };
  // getTask();

  const [tasks, setTasks] = useState([])
  useEffect( () => {
      axios
      .get("http://localhost:8000/api/tasks/")
      .then((data) => setTasks(data.data) )
  }, [])

  function handleAdd(task) {
    const newTask = { id : tasks.length + 1, name : task.task, description: '', completed: false,  };
    tasks.append(newTask);
    console.log(tasks);
  }

  function handleToggle(task) {
    const updatedTasks = tasks.map((t) => {
      if(t.id === task.id) {
        return {...t, completed: !t.completed};
      }
      return t;
    });
    setTasks(updatedTasks);
  }

  return (
    <div className="App">
      <header className="App-header"> TODO APP </header>
      <InputTask onAdd={handleAdd} />
      <ListTask tasks={tasks} onToggle={handleToggle} />
    </div>
  );
}

const data = [
  {
    title: 'something1',
  },
  {
    title: 'something2',
  },
  {
    title: 'something3',
  },
  {
    title: 'something4',
  }
]

export default App;
