import { Checkbox, List } from "antd";

export const ListTask = ({tasks, onToggle}) => {
    return (
        <List
            dataSource={tasks} 
            renderItem={(task) => (
                <List.Item>
                    <Checkbox checked={task.completed} onChange={() => onToggle(task)} />
                    <span style={{ textDecoration: task.completed ? 'line-through' : 'none' }} >
                        {task.name}
                    </span>
                </List.Item>
            )}
        />
    );
};
